TwelveHourField(NumberRule rule) {
mRule = rule;
}

public int estimateLength() {
return mRule.estimateLength();
}

public void appendTo(StringBuffer buffer, Calendar calendar) {
int value = calendar.get(Calendar.HOUR);
if (value == 0) {
value = calendar.getLeastMaximum(Calendar.HOUR) + 1;
}
mRule.appendTo(buffer, value);
}

TextField(int field, String[] values) {
mField = field;
mValues = values;
}

public int estimateLength() {
int max = 0;
for (int i=mValues.length; --i >= 0; ) {
int len = mValues[i].length();
if (len > max) {
max = len;
}
}
return max;
}

public void appendTo(StringBuffer buffer, Calendar calendar) {
buffer.append(mValues[calendar.get(mField)]);
}

TimeZoneNameRule(TimeZone timeZone, Locale locale, int style) {
mLocale = locale;
mStyle = style;
zone = timeZone;

mStandard = getTimeZoneDisplay(timeZone, false, style, locale);
mDaylight = getTimeZoneDisplay(timeZone, true, style, locale);
}

public int estimateLength() {
// We have no access to the Calendar object that will be passed to
// appendTo so base estimate on the TimeZone passed to the
// constructor
return Math.max(mStandard.length(), mDaylight.length());
}

public void appendTo(StringBuffer buffer, Calendar calendar) {
if (zone.useDaylightTime()
&& calendar.get(Calendar.DST_OFFSET) != 0) {
buffer.append(getTimeZoneDisplay(zone, true, mStyle, mLocale));
} else {
buffer.append(getTimeZoneDisplay(zone, false, mStyle, mLocale));
}
}

TimeZoneDisplayKey(TimeZone timeZone,
boolean daylight, int style, Locale locale) {
mTimeZone = timeZone;
if (daylight) {
style |= 0x80000000;
}
mStyle = style;
mLocale = locale;
}

public int hashCode() {
return (mStyle * 31 + mLocale.hashCode() ) * 31 + mTimeZone.hashCode();
}

public boolean equals(Object obj) {
if (this == obj) {
return true;
}
if (obj instanceof TimeZoneDisplayKey) {
TimeZoneDisplayKey other = (TimeZoneDisplayKey)obj;
return
mTimeZone.equals(other.mTimeZone) &&
mStyle == other.mStyle &&
mLocale.equals(other.mLocale);
}
return false;
}

TextStrategy(int field, Calendar definingCalendar, Locale locale) {
this.field= field;
this.keyValues= getDisplayNames(field, definingCalendar, locale);
}

public boolean isNumber() {
return false;
}

public boolean addRegex(FastDateParser parser, StringBuilder regex) {
regex.append('(');
for(String textKeyValue : keyValues.keySet()) {
escapeRegex(regex, textKeyValue, false).append('|');
}
regex.setCharAt(regex.length()-1, ')');
