public TarArchiveEntry getNextTarEntry() throws IOException {
if (hasHitEOF) {
return null;
}

if (currEntry != null) {
long numToSkip = entrySize - entryOffset;

while (numToSkip > 0) {
long skipped = skip(numToSkip);
if (skipped <= 0) {
throw new RuntimeException("failed to skip current tar"
+ " entry");
}
numToSkip -= skipped;
}

readBuf = null;
}

byte[] headerBuf = getRecord();

if (hasHitEOF) {
currEntry = null;
return null;
}

try {
currEntry = new TarArchiveEntry(headerBuf, encoding);
} catch (IllegalArgumentException e) {
IOException ioe = new IOException("Error detected parsing the header");
ioe.initCause(e);
throw ioe;
}
entryOffset = 0;
entrySize = currEntry.getSize();

if (currEntry.isGNULongNameEntry()) {
// read in the name
StringBuffer longName = new StringBuffer();
byte[] buf = new byte[SMALL_BUFFER_SIZE];
int length = 0;
while ((length = read(buf)) >= 0) {
longName.append(new String(buf, 0, length)); // TODO default charset?
}
getNextEntry();
if (currEntry == null) {
// Bugzilla: 40334
// Malformed tar file - long entry name not followed by entry
return null;
}
// remove trailing null terminator
if (longName.length() > 0
&& longName.charAt(longName.length() - 1) == 0) {
longName.deleteCharAt(longName.length() - 1);
}
currEntry.setName(longName.toString());
}

if (currEntry.isPaxHeader()){ // Process Pax headers
paxHeaders();
}

if (currEntry.isGNUSparse()){ // Process sparse files
readGNUSparse();
}

// If the size of the next element in the archive has changed
// due to a new size being reported in the posix header
// information, we update entrySize here so that it contains
// the correct value.
entrySize = currEntry.getSize();
return currEntry;
}

public static boolean matches(byte[] signature, int length) {
if (length < 6) {
return false;
}

// Check binary values
if (signature[0] == 0x71 && (signature[1] & 0xFF) == 0xc7) {
return true;
}
if (signature[1] == 0x71 && (signature[0] & 0xFF) == 0xc7) {
return true;
}

// Check Ascii (String) values
// 3037 3037 30nn
if (signature[0] != 0x30) {
return false;
}
if (signature[1] != 0x37) {
return false;
}
if (signature[2] != 0x30) {
return false;
}
if (signature[3] != 0x37) {
return false;
}
if (signature[4] != 0x30) {
return false;
}
// Check last byte
if (signature[5] == 0x31) {
return true;
}
if (signature[5] == 0x32) {
return true;
}
if (signature[5] == 0x37) {
return true;
}

return false;
}

public static boolean verifyCheckSum(byte[] header) {
long storedSum = 0;
long unsignedSum = 0;
long signedSum = 0;

int digits = 0;
for (int i = 0; i < header.length; i++) {
byte b = header[i];
if (CHKSUM_OFFSET  <= i && i < CHKSUM_OFFSET + CHKSUMLEN) {
if ('0' <= b && b <= '7' && digits++ < 6) {
storedSum = storedSum * 8 + b - '0';
} else if (digits > 0) {
digits = 6; // only look at the first octal digit sequence
}
b = ' ';
}
unsignedSum += 0xff & b;
signedSum += b;
}

return storedSum == unsignedSum || storedSum == signedSum
|| storedSum > unsignedSum; // COMPRESS-177
}

