final int start = pos;
final int remaining = bufLength;
final char[] val = charBuf;

OUTER: while (pos < remaining) {
switch (val[pos]) {
case '\t':
case '\n':
case '\r':
case '\f':
case ' ':
case '/':
case '>':
case '<':
case TokeniserState.nullChar:
break OUTER;
}
pos++;
}

bufPos = pos;
return pos > start ? cacheString(charBuf, stringCache, start, pos -start) : "";
}

