if (isMinim ?
fA < fB :
fA > fB) {

double tmp = xA;
xA = xB;
xB = tmp;

tmp = fA;
fA = fB;
fB = tmp;
}

double xC = xB + GOLD * (xB - xA);
double fC = eval(func, xC);

while (isMinim ? fC < fB : fC > fB) {
double tmp1 = (xB - xA) * (fB - fC);
double tmp2 = (xB - xC) * (fB - fA);

double val = tmp2 - tmp1;
