public static long abs(final long x) {
return (x < 0l) ? -x : x;
}

public Incrementor(int max) {
this(max,
new MaxCountExceededCallback() {
/** {@inheritDoc} */
public void trigger(int max) {
throw new MaxCountExceededException(max);
}
});
}

public double getStartValue() {
return searchStart;
}

public TooManyEvaluationsException(Number max) {
super(max);
getContext().addMessage(LocalizedFormats.EVALUATIONS);
}

public ExceptionContext(final Throwable throwable) {
this.throwable = throwable;
msgPatterns    = new ArrayList<Localizable>();
msgArguments   = new ArrayList<Object[]>();
context        = new HashMap<String, Object>();
}

protected double computeObjectiveValue(double point)
throws TooManyEvaluationsException {
incrementEvaluationCount();
return function.value(point);
}

public static void verifySequence(final double lower,
final double initial,
final double upper) {
verifyInterval(lower, initial);
verifyInterval(initial, upper);
}

public static double max(final double a, final double b) {
if (a > b) {
return a;
}
if (a < b) {
return b;
}
/* if either arg is NaN, return NaN */
if (a != b) {
return Double.NaN;
}
/* min(+0.0,-0.0) == -0.0 */
/* 0x8000000000000000L == Double.doubleToRawLongBits(-0.0d) */
long bits = Double.doubleToRawLongBits(a);
if (bits == 0x8000000000000000L) {
return b;
}
return a;
}

protected BaseAbstractUnivariateRealSolver(final double relativeAccuracy,
final double absoluteAccuracy,
final double functionValueAccuracy) {
this.absoluteAccuracy = absoluteAccuracy;
this.relativeAccuracy = relativeAccuracy;
this.functionValueAccuracy = functionValueAccuracy;
}

public void addMessage(Localizable pattern,
Object ... arguments) {
msgPatterns.add(pattern);
msgArguments.add(ArgUtils.flatten(arguments));
}

public ExceptionContext getContext() {
return context;
}

public static Object[] flatten(Object[] array) {
final List<Object> list = new ArrayList<Object>();
if (array != null) {
for (Object o : array) {
if (o instanceof Object[]) {
for (Object oR : flatten((Object[]) o)) {
list.add(oR);
}
} else {
list.add(o);
}
}
}
return list.toArray();
}

public MathIllegalStateException(Localizable pattern,
Object ... args) {
context = new ExceptionContext(this);
context.addMessage(pattern, args);
}

public MathIllegalStateException() {
this(LocalizedFormats.ILLEGAL_STATE);
}

public static void checkNotNull(Object o)
throws NullArgumentException {
if (o == null) {
throw new NullArgumentException();
}
}

private double guessX(final double targetY, final double[] x, final double[] y,
final int start, final int end) {

// compute Q Newton coefficients by divided differences
for (int i = start; i < end - 1; ++i) {
final int delta = i + 1 - start;
for (int j = end - 1; j > i; --j) {
x[j] = (x[j] - x[j-1]) / (y[j] - y[j - delta]);
}
}

// evaluate Q(targetY)
double x0 = 0;
for (int j = end - 1; j >= start; --j) {
x0 = x[j] + x0 * (targetY - y[j]);
}

return x0;

}

public void incrementCount() {
if (++count > maximalCount) {
maxCountCallback.trigger(maximalCount);
}
}

public double getAbsoluteAccuracy() {
return absoluteAccuracy;
}

protected void verifySequence(final double lower,
final double initial,
final double upper) {
UnivariateRealSolverUtils.verifySequence(lower, initial, upper);
}

public static boolean equals(double x, double y, int maxUlps) {
long xInt = Double.doubleToLongBits(x);
long yInt = Double.doubleToLongBits(y);

// Make lexicographically ordered as a two's-complement integer.
if (xInt < 0) {
xInt = SGN_MASK - xInt;
}
if (yInt < 0) {
yInt = SGN_MASK - yInt;
}

final boolean isEqual = FastMath.abs(xInt - yInt) <= maxUlps;

return isEqual && !Double.isNaN(x) && !Double.isNaN(y);
}

public MaxCountExceededException(Localizable specific,
Number max,
Object ... args) {
getContext().addMessage(specific, max, args);
this.max = max;
}

protected AbstractUnivariateRealSolver(final double relativeAccuracy,
final double absoluteAccuracy,
final double functionValueAccuracy) {
super(relativeAccuracy, absoluteAccuracy, functionValueAccuracy);
}

public double getMin() {
return searchMin;
}

protected void setup(int maxEval,
FUNC f,
double min, double max,
double startValue) {
// Checks.
MathUtils.checkNotNull(f);

// Reset.
searchMin = min;
searchMax = max;
searchStart = startValue;
function = f;
evaluations.setMaximalCount(maxEval);
evaluations.resetCount();
}

protected double doSolve() {

// prepare arrays with the first points
final double[] x = new double[maximalOrder + 1];
final double[] y = new double[maximalOrder + 1];
x[0] = getMin();
x[1] = getStartValue();
x[2] = getMax();
verifySequence(x[0], x[1], x[2]);

// evaluate initial guess
y[1] = computeObjectiveValue(x[1]);
if (Precision.equals(y[1], 0.0, 1)) {
// return the initial guess if it is a perfect root.
return x[1];
}

// evaluate first  endpoint
y[0] = computeObjectiveValue(x[0]);
if (Precision.equals(y[0], 0.0, 1)) {
// return the first endpoint if it is a perfect root.
return x[0];
}

int nbPoints;
int signChangeIndex;
if (y[0] * y[1] < 0) {

// reduce interval if it brackets the root
nbPoints        = 2;
signChangeIndex = 1;

} else {

// evaluate second endpoint
y[2] = computeObjectiveValue(x[2]);
if (Precision.equals(y[2], 0.0, 1)) {
// return the second endpoint if it is a perfect root.
return x[2];
}

if (y[1] * y[2] < 0) {
// use all computed point as a start sampling array for solving
nbPoints        = 3;
signChangeIndex = 2;
} else {
throw new NoBracketingException(x[0], x[2], y[0], y[2]);
}

}

// prepare a work array for inverse polynomial interpolation
final double[] tmpX = new double[x.length];

// current tightest bracketing of the root
double xA    = x[signChangeIndex - 1];
double yA    = y[signChangeIndex - 1];
double absYA = FastMath.abs(yA);
int agingA   = 0;
double xB    = x[signChangeIndex];
double yB    = y[signChangeIndex];
double absYB = FastMath.abs(yB);
int agingB   = 0;

// search loop
while (true) {

// check convergence of bracketing interval
final double xTol = getAbsoluteAccuracy() +
getRelativeAccuracy() * FastMath.max(FastMath.abs(xA), FastMath.abs(xB));
if (((xB - xA) <= xTol) || (FastMath.max(absYA, absYB) < getFunctionValueAccuracy())) {
switch (allowed) {
case ANY_SIDE :
return absYA < absYB ? xA : xB;
case LEFT_SIDE :
return xA;
case RIGHT_SIDE :
return xB;
case BELOW_SIDE :
return (yA <= 0) ? xA : xB;
case ABOVE_SIDE :
return (yA <  0) ? xB : xA;
default :
// this should never happen
throw new MathInternalError(null);
}
}

// target for the next evaluation point
double targetY;
if (agingA >= MAXIMAL_AGING) {
// we keep updating the high bracket, try to compensate this
targetY = -REDUCTION_FACTOR * yB;
} else if (agingB >= MAXIMAL_AGING) {
// we keep updating the low bracket, try to compensate this
targetY = -REDUCTION_FACTOR * yA;
} else {
// bracketing is balanced, try to find the root itself
targetY = 0;
}

// make a few attempts to guess a root,
double nextX;
int start = 0;
int end   = nbPoints;
do {

// guess a value for current target, using inverse polynomial interpolation
System.arraycopy(x, start, tmpX, start, end - start);
nextX = guessX(targetY, tmpX, y, start, end);

if (!((nextX > xA) && (nextX < xB))) {
// the guessed root is not strictly inside of the tightest bracketing interval

// the guessed root is either not strictly inside the interval or it
// is a NaN (which occurs when some sampling points share the same y)
// we try again with a lower interpolation order
if (signChangeIndex - start >= end - signChangeIndex) {
// we have more points before the sign change, drop the lowest point
++start;
} else {
// we have more points after sign change, drop the highest point
--end;
}

// we need to do one more attempt
nextX = Double.NaN;

}

} while (Double.isNaN(nextX) && (end - start > 1));

if (Double.isNaN(nextX)) {
// fall back to bisection
nextX = xA + 0.5 * (xB - xA);
start = signChangeIndex - 1;
end   = signChangeIndex;
}

// evaluate the function at the guessed root
final double nextY = computeObjectiveValue(nextX);
if (Precision.equals(nextY, 0.0, 1)) {
// we have found an exact root, since it is not an approximation
// we don't need to bother about the allowed solutions setting
return nextX;
}

if ((nbPoints > 2) && (end - start != nbPoints)) {

// we have been forced to ignore some points to keep bracketing,
// they are probably too far from the root, drop them from now on
nbPoints = end - start;
System.arraycopy(x, start, x, 0, nbPoints);
System.arraycopy(y, start, y, 0, nbPoints);
signChangeIndex -= start;

} else  if (nbPoints == x.length) {

// we have to drop one point in order to insert the new one
nbPoints--;

// keep the tightest bracketing interval as centered as possible
if (signChangeIndex >= (x.length + 1) / 2) {
// we drop the lowest point, we have to shift the arrays and the index
System.arraycopy(x, 1, x, 0, nbPoints);
System.arraycopy(y, 1, y, 0, nbPoints);
--signChangeIndex;
}

}

// insert the last computed point
//(by construction, we know it lies inside the tightest bracketing interval)
System.arraycopy(x, signChangeIndex, x, signChangeIndex + 1, nbPoints - signChangeIndex);
x[signChangeIndex] = nextX;
System.arraycopy(y, signChangeIndex, y, signChangeIndex + 1, nbPoints - signChangeIndex);
y[signChangeIndex] = nextY;
++nbPoints;

// update the bracketing interval
if (nextY * yA <= 0) {
// the sign change occurs before the inserted point
xB = nextX;
yB = nextY;
absYB = FastMath.abs(yB);
++agingA;
agingB = 0;
} else {
// the sign change occurs after the inserted point
xA = nextX;
yA = nextY;
absYA = FastMath.abs(yA);
agingA = 0;
++agingB;

// update the sign change index
signChangeIndex++;

}

}

}

public double getRelativeAccuracy() {
return relativeAccuracy;
}

public static void verifyInterval(final double lower,
final double upper) {
if (lower >= upper) {
throw new NumberIsTooLargeException(LocalizedFormats.ENDPOINTS_NOT_AN_INTERVAL,
lower, upper, false);
}
}

public double getMax() {
return searchMax;
}

public double getFunctionValueAccuracy() {
return functionValueAccuracy;
}

public Incrementor() {
this(0);
}

public double solve(int maxEval, FUNC f, double min, double max, double startValue) {
// Initialization.
setup(maxEval, f, min, max, startValue);

// Perform computation.
return doSolve();
}

protected void incrementEvaluationCount() {
try {
evaluations.incrementCount();
} catch (MaxCountExceededException e) {
throw new TooManyEvaluationsException(e.getMax());
}
}

public static double abs(double x) {
return (x < 0.0) ? -x : (x == 0.0) ? 0.0 : x; // -0.0 => +0.0
}

public Incrementor(int max,
MaxCountExceededCallback cb) {
maximalCount = max;
maxCountCallback = cb;
}

public void resetCount() {
count = 0;
}

public void setMaximalCount(int max) {
maximalCount = max;
}

public void trigger(int max) {
throw new MaxCountExceededException(max);
}

public MaxCountExceededException(Number max) {
this(LocalizedFormats.MAX_COUNT_EXCEEDED, max);
}

public BracketingNthOrderBrentSolver(final double relativeAccuracy,
final double absoluteAccuracy,
final double functionValueAccuracy,
final int maximalOrder)
throws NumberIsTooSmallException {
super(relativeAccuracy, absoluteAccuracy, functionValueAccuracy);
if (maximalOrder < 2) {
throw new NumberIsTooSmallException(maximalOrder, 2, true);
}
this.maximalOrder = maximalOrder;
this.allowed = AllowedSolution.ANY_SIDE;
}

public Number getMax() {
return max;
}

public double solve(int maxEval, UnivariateFunction f, double min,
double max, double startValue,
AllowedSolution allowedSolution) {
this.allowed = allowedSolution;
return super.solve(maxEval, f, min, max, startValue);
}
