public CommandLine parse(Options options, String[] arguments,
Properties properties, boolean stopAtNonOption)
throws ParseException
{
// initialise members
this.options = options;
requiredOptions = options.getRequiredOptions();
cmd = new CommandLine();

boolean eatTheRest = false;

if (arguments == null)
{
arguments = new String[0];
}

List tokenList = Arrays.asList(flatten(this.options,
arguments,
stopAtNonOption));

ListIterator iterator = tokenList.listIterator();

// process each flattened token
while (iterator.hasNext())
{
String t = (String) iterator.next();

// the value is the double-dash
if ("--".equals(t))
{
eatTheRest = true;
}

// the value is a single dash
else if ("-".equals(t))
{
if (stopAtNonOption)
{
eatTheRest = true;
}
else
{
cmd.addArg(t);
}
}

// the value is an option
else if (t.startsWith("-"))
{
if (stopAtNonOption && !options.hasOption(t))
{
eatTheRest = true;
cmd.addArg(t);
}
else
{
processOption(t, iterator);
}
}

// the value is an argument
else
{
cmd.addArg(t);

if (stopAtNonOption)
{
eatTheRest = true;
}
}

// eat the remaining tokens
if (eatTheRest)
{
while (iterator.hasNext())
{
String str = (String) iterator.next();

// ensure only one double-dash is added
if (!"--".equals(str))
{
cmd.addArg(str);
}
}
}
}

processProperties(properties);
checkRequiredOptions();

return cmd;
}

protected String[] flatten(Options options, String[] arguments,
boolean stopAtNonOption)
{
init();
this.options = options;

// an iterator for the command line tokens
Iterator iter = Arrays.asList(arguments).iterator();
String token = null;

// process each command line token
while (iter.hasNext())
{
// get the next command line token
token = (String) iter.next();

// handle SPECIAL TOKEN
if (token.startsWith("--"))
{
if (token.indexOf('=') != -1)
{
tokens.add(token.substring(0, token.indexOf('=')));
tokens.add(token.substring(token.indexOf('=') + 1,
token.length()));
}
else
{
tokens.add(token);
}
}

// single hyphen
else if ("-".equals(token))
{
processSingleHyphen(token);
}
else if (token.startsWith("-"))
{
int tokenLength = token.length();

if (tokenLength == 2)
{
processOptionToken(token, stopAtNonOption);
}
else if (options.hasOption(token)) {
tokens.add(token);
}
// requires bursting
else
{
burstToken(token, stopAtNonOption);
}
}
else
{
if (stopAtNonOption)
{
process(token);
}
else
{
tokens.add(token);
}
}

gobble(iter);
}

return (String[]) tokens.toArray(new String[] {  });
}

private void processProperties(Properties properties)
{
if (properties == null)
{
return;
}

for (Enumeration e = properties.propertyNames(); e.hasMoreElements();)
{
String option = e.nextElement().toString();

if (!cmd.hasOption(option))
{
Option opt = options.getOption(option);

// get the value from the properties instance
String value = properties.getProperty(option);

if (opt.hasArg())
{
if ((opt.getValues() == null)
|| (opt.getValues().length == 0))
{
try
{
opt.addValue(value);
}
catch (RuntimeException exp)
{
// if we cannot add the value don't worry about it
}
}
}
else if (!("yes".equalsIgnoreCase(value)
|| "true".equalsIgnoreCase(value)
|| "1".equalsIgnoreCase(value)))
{
// if the value is not yes, true or 1 then don't add the
// option to the CommandLine
break;
}

cmd.addOption(opt);
}
}
}

private void processOption(String arg, ListIterator iter)
throws ParseException
{
boolean hasOption = options.hasOption(arg);

// if there is no option throw an UnrecognisedOptionException
if (!hasOption)
{
throw new UnrecognizedOptionException("Unrecognized option: "
+ arg);
}

// get the option represented by arg
final Option opt = options.getOption(arg);

// if the option is a required option remove the option from
// the requiredOptions list
if (opt.isRequired())
{
requiredOptions.remove(opt.getKey());
}

// if the option is in an OptionGroup make that option the selected
// option of the group
if (options.getOptionGroup(opt) != null)
{
OptionGroup group = options.getOptionGroup(opt);

if (group.isRequired())
{
requiredOptions.remove(group);
}

group.setSelected(opt);
}

// if the option takes an argument value
if (opt.hasArg())
{
processArgs(opt, iter);
}


// set the option on the command line
cmd.addOption(opt);
}

private void processValue(String value)
{
// this Option has a separator character
if (hasValueSeparator())
{
// get the separator character
char sep = getValueSeparator();

// store the index for the value separator
int index = value.indexOf(sep);

// while there are more value separators
while (index != -1)
{
// next value to be added
if (values.size() == (numberOfArgs - 1))
{
break;
}


// store
add(value.substring(0, index));


// parse
value = value.substring(index + 1);


// get new index
index = value.indexOf(sep);
}
}


// store the actual value or the last value that has been parsed
add(value);
}

static void validateOption(String opt)
throws IllegalArgumentException
{
// check that opt is not NULL
if (opt == null)
{
return;
}

// handle the single character opt
else if (opt.length() == 1)
{
char ch = opt.charAt(0);

if (!isValidOpt(ch))
{
throw new IllegalArgumentException("illegal option value '" + ch
+ "'");
}
}

// handle the multi character opt
else
{
char[] chars = opt.toCharArray();

for (int i = 0; i < chars.length; i++)
{
if (!isValidChar(chars[i]))
{
throw new IllegalArgumentException(
"opt contains illegal character value '" + chars[i]
+ "'");
}
}
}
}

public void processArgs(Option opt, ListIterator iter)
throws ParseException
{
// loop until an option is found
while (iter.hasNext())
{
String str = (String) iter.next();

// found an Option, not an argument
if (options.hasOption(str) && str.startsWith("-"))
{
iter.previous();
break;
}

// found a value
try
{
opt.addValue( Util.stripLeadingAndTrailingQuotes(str) );
}
catch (RuntimeException exp)
{
iter.previous();
break;
}
}

if ((opt.getValues() == null) && !opt.hasOptionalArg())
{
throw new MissingArgumentException("Missing argument for option:"
+ opt.getKey());
}
}

private static String longopt;

/** option description */
private static String description;

/** argument name */
private static String argName;

/** is required? */
private static boolean required;

/** the number of arguments */
private static int numberOfArgs = Option.UNINITIALIZED;

/** option type */
private static Object type;

/** option can have an optional argument value */
private static boolean optionalArg;

/** value separator for argument value */
private static char valuesep;

/** option builder instance */
private static OptionBuilder instance = new OptionBuilder();

/**
* private constructor to prevent instances being created
*/
private OptionBuilder()
{
// hide the constructor
}

public static Option create(String opt)
throws IllegalArgumentException
{
// create the option
Option option = new Option(opt, description);


// set the option properties
option.setLongOpt(longopt);
option.setRequired(required);
option.setOptionalArg(optionalArg);
option.setArgs(numberOfArgs);
option.setType(type);
option.setValueSeparator(valuesep);
option.setArgName(argName);


// reset the OptionBuilder properties
OptionBuilder.reset();

// return the Option instance
return option;
}

public Options addOption(Option opt)
{
String key = opt.getKey();

// add it to the long option list
if (opt.hasLongOpt())
{
longOpts.put(opt.getLongOpt(), opt);
}

// if the option is required add it to the required list
if (opt.isRequired() )
{
if( requiredOpts.contains(key) ) {
requiredOpts.remove( requiredOpts.indexOf(key) );
}
requiredOpts.add(key);
}

shortOpts.put(key, opt);

return this;
}

private void checkRequiredOptions()
throws MissingOptionException
{
// if there are required options that have not been
// processsed
if (requiredOptions.size() > 0)
{
Iterator iter = requiredOptions.iterator();
StringBuffer buff = new StringBuffer();

// loop through the required options
while (iter.hasNext())
{
buff.append(iter.next());
}

throw new MissingOptionException(buff.toString());
}
}

public Option(String opt, String longOpt, boolean hasArg,
String description)
throws IllegalArgumentException
{
// ensure that the option is valid
OptionValidator.validateOption(opt);

this.opt = opt;
this.longOpt = longOpt;

// if hasArg is set then the number of arguments is 1
if (hasArg)
{
this.numberOfArgs = 1;
}

this.hasArg = hasArg;
this.description = description;
}

public String[] getOptionValues(String opt)
{
opt = Util.stripLeadingHyphens(opt);

String key = opt;
if (names.containsKey(opt))
{
key = (String) names.get(opt);
}
if (options.containsKey(key))
{
return ((Option) options.get(key)).getValues();
}

return null;
}

void addOption(Option opt)
{
hashcodeMap.put(new Integer(opt.hashCode()), opt);
String key = opt.getKey();
if (key == null)
{
key = opt.getLongOpt();
}
else
{
names.put(opt.getLongOpt(), key);
}
options.put(key, opt);
}

private static void reset()
{
description = null;
argName = "arg";
longopt = null;
type = null;
required = false;
numberOfArgs = Option.UNINITIALIZED;


// PMM 9/6/02 - these were missing
optionalArg = false;
valuesep = (char) 0;
}

static String stripLeadingHyphens(String str)
{
if (str.startsWith("--"))
{
return str.substring(2, str.length());
}
else if (str.startsWith("-"))
{
return str.substring(1, str.length());
}

return str;
}

private void processOptionToken(String token, boolean stopAtNonOption)
{
if (this.options.hasOption(token))
{
currentOption = this.options.getOption(token);
tokens.add(token);
}
else if (stopAtNonOption)
{
eatTheRest = true;
}
}

public Option getOption(String opt)
{
opt = Util.stripLeadingHyphens(opt);

if (shortOpts.containsKey(opt))
{
return (Option) shortOpts.get(opt);
}

return (Option) longOpts.get(opt);
}

void addValue(String value)
{
switch (numberOfArgs)
{
case UNINITIALIZED:
throw new RuntimeException("NO_ARGS_ALLOWED");

default:
processValue(value);
}
}

private void add(String value)
{
if ((numberOfArgs > 0) && (values.size() > (numberOfArgs - 1)))
{
throw new RuntimeException("Cannot add value, list full.");
}


// store value
this.values.add(value);
}

private void gobble(Iterator iter)
{
if (eatTheRest)
{
while (iter.hasNext())
{
tokens.add(iter.next());
}
}
}

String getKey()
{
// if 'opt' is null, then it is a 'long' option
if (opt == null)
{
return this.longOpt;
}

return this.opt;
}

static String stripLeadingAndTrailingQuotes(String str)
{
if (str.startsWith("\"")) {
str = str.substring(1, str.length());
}
if (str.endsWith("\"")) {
str = str.substring(0, str.length()-1);
}
return str;
}

public int hashCode()
{
int result;
result = ( opt != null ? opt.hashCode() : 0 );
result = 31 * result + ( longOpt != null ? longOpt.hashCode() : 0 );
return result;
}

public String getOptionValue(String opt)
{
String[] values = getOptionValues(opt);

return (values == null) ? null : values[0];
}

private void init()
{
eatTheRest = false;
tokens.clear();
currentOption = null;
}

public boolean hasOption(String opt)
{
opt = Util.stripLeadingHyphens(opt);

return shortOpts.containsKey(opt) || longOpts.containsKey(opt);
}

public static OptionBuilder withLongOpt(String newLongopt)
{
OptionBuilder.longopt = newLongopt;

return instance;
}

public static OptionBuilder hasArg()
{
OptionBuilder.numberOfArgs = 1;

return instance;
}

public static OptionBuilder withArgName(String name)
{
OptionBuilder.argName = name;

return instance;
}

public static OptionBuilder withDescription(String newDescription)
{
OptionBuilder.description = newDescription;

return instance;
}

public static Option create(char opt)
throws IllegalArgumentException
{
return create(String.valueOf(opt));
}

public Option(String opt, String description)
throws IllegalArgumentException
{
this(opt, null, false, description);
}

private ArrayList tokens = new ArrayList();
eatTheRest = false;
tokens.clear();
currentOption = null;
}

public String[] getValues()
{
return hasNoValues()
? null : (String[]) this.values.toArray(new String[] {  });
}

public CommandLine parse(Options options, String[] arguments)
throws ParseException
{
return parse(options, arguments, null, false);
}

public boolean hasOption(String opt)
{
return options.containsKey(opt);
}

public Options()
{
// nothing to do
}

private boolean hasNoValues()
{
return this.values.size() == 0;
}

public String getOptionValue(char opt)
{
return getOptionValue(String.valueOf(opt));
}

public boolean hasOption(char opt)
{
return hasOption(String.valueOf(opt));
}

public List getRequiredOptions()
{
return requiredOpts;
}

public OptionGroup getOptionGroup(Option opt)
{
return (OptionGroup) optionGroups.get(opt.getKey());
}

public void setType(Object type)
{
this.type = type;
}

public String getLongOpt()
{
return this.longOpt;
}

public void setLongOpt(String longOpt)
{
this.longOpt = longOpt;
}

public void setOptionalArg(boolean optionalArg)
{
this.optionalArg = optionalArg;
}

public boolean hasLongOpt()
{
return (this.longOpt != null);
}

public boolean hasArg()
{
return (this.numberOfArgs > 0) || (numberOfArgs == UNLIMITED_VALUES);
}

public boolean isRequired()
{
return this.required;
}

public void setRequired(boolean required)
{
this.required = required;
}

public void setArgName(String argName)
{
this.argName = argName;
}

public void setArgs(int num)
{
this.numberOfArgs = num;
}

public void setValueSeparator(char sep)
{
this.valuesep = sep;
}

public boolean hasValueSeparator()
{
return (this.valuesep > 0);
}

private static boolean isValidOpt(char c)
{
return (isValidChar(c) || (c == ' ') || (c == '?') || c == '@');
}

private static boolean isValidChar(char c)
{
return Character.isJavaIdentifierPart(c);
}

private OptionBuilder()
{
// hide the constructor
}

CommandLine()
{
// nothing to do
}
