}

public void validateState() {
if (!matcherStack.isEmpty()) {
LocalizedMatcher lastMatcher = matcherStack.lastElement();
matcherStack.clear();
new Reporter().misplacedArgumentMatcher(lastMatcher.getLocation());
}
}

