printEst += printer.estimatePrintedLength();
iPrinters[i] = printer;
}
iPrintedLengthEstimate = printEst;
}

if (parserList.contains(null) || parserList.isEmpty()) {
iParsers = null;
iParsedLengthEstimate = 0;
} else {
int size = parserList.size();
iParsers = new DateTimeParser[size];
int parseEst = 0;
for (int i=0; i<size; i++) {
DateTimeParser parser = (DateTimeParser) parserList.get(i);
parseEst += parser.estimateParsedLength();
iParsers[i] = parser;
}
iParsedLengthEstimate = parseEst;
}
}

int getYear(long instant) {
// Get an initial estimate of the year, and the millis value that
// represents the start of that year. Then verify estimate and fix if
// necessary.

// Initial estimate uses values divided by two to avoid overflow.
long unitMillis = getAverageMillisPerYearDividedByTwo();
long i2 = (instant >> 1) + getApproxMillisAtEpochDividedByTwo();
if (i2 < 0) {
i2 = i2 - unitMillis + 1;
}
int year = (int) (i2 / unitMillis);

long yearStart = getYearMillis(year);
long diff = instant - yearStart;

if (diff < 0) {
year--;
} else if (diff >= DateTimeConstants.MILLIS_PER_DAY * 365L) {
// One year may need to be added to fix estimate.
long oneYear;
if (isLeapYear(year)) {
oneYear = DateTimeConstants.MILLIS_PER_DAY * 366L;
} else {
oneYear = DateTimeConstants.MILLIS_PER_DAY * 365L;
}

yearStart += oneYear;

if (yearStart <= instant) {
// Didn't go too far, so actually add one year.
year++;
}
}

return year;
}

public void validate(ReadablePartial partial, int[] values) {
// check values in standard range, catching really stupid cases like -1
// this means that the second check will not hit trouble
int size = partial.size();
for (int i = 0; i < size; i++) {
int value = values[i];
DateTimeField field = partial.getField(i);
if (value < field.getMinimumValue()) {
throw new IllegalFieldValueException
(field.getType(), Integer.valueOf(value),
Integer.valueOf(field.getMinimumValue()), null);
}
if (value > field.getMaximumValue()) {
throw new IllegalFieldValueException
(field.getType(), Integer.valueOf(value),
null, Integer.valueOf(field.getMaximumValue()));
}
}
// check values in specific range, catching really odd cases like 30th Feb
for (int i = 0; i < size; i++) {
int value = values[i];
DateTimeField field = partial.getField(i);
if (value < field.getMinimumValue(partial, values)) {
throw new IllegalFieldValueException
(field.getType(), Integer.valueOf(value),
Integer.valueOf(field.getMinimumValue(partial, values)), null);
}
if (value > field.getMaximumValue(partial, values)) {
throw new IllegalFieldValueException
(field.getType(), Integer.valueOf(value),
null, Integer.valueOf(field.getMaximumValue(partial, values)));
}
}
}

