public String doubleMetaphone(String value, boolean alternate) {
value = cleanInput(value);
if (value == null) {
return null;
}

boolean slavoGermanic = isSlavoGermanic(value);
int index = isSilentStart(value) ? 1 : 0;

DoubleMetaphoneResult result = new DoubleMetaphoneResult(this.getMaxCodeLen());

while (!result.isComplete() && index <= value.length() - 1) {
switch (value.charAt(index)) {
case 'A':
case 'E':
case 'I':
case 'O':
case 'U':
case 'Y':
index = handleAEIOUY(value, result, index);
break;
case 'B':
result.append('P');
index = charAt(value, index + 1) == 'B' ? index + 2 : index + 1;
break;
case '\u00C7':
// A C with a Cedilla
result.append('S');
index++;
break;
case 'C':
index = handleC(value, result, index);
break;
case 'D':
index = handleD(value, result, index);
break;
case 'F':
result.append('F');
index = charAt(value, index + 1) == 'F' ? index + 2 : index + 1;
break;
case 'G':
index = handleG(value, result, index, slavoGermanic);
break;
case 'H':
index = handleH(value, result, index);
break;
case 'J':
index = handleJ(value, result, index, slavoGermanic);
break;
case 'K':
result.append('K');
index = charAt(value, index + 1) == 'K' ? index + 2 : index + 1;
break;
case 'L':
index = handleL(value, result, index);
break;
case 'M':
result.append('M');
index = conditionM0(value, index) ? index + 2 : index + 1;
break;
case 'N':
result.append('N');
index = charAt(value, index + 1) == 'N' ? index + 2 : index + 1;
break;
case '\u00D1':
// N with a tilde (spanish ene)
result.append('N');
index++;
break;
case 'P':
index = handleP(value, result, index);
break;
case 'Q':
result.append('K');
index = charAt(value, index + 1) == 'Q' ? index + 2 : index + 1;
break;
case 'R':
index = handleR(value, result, index, slavoGermanic);
break;
case 'S':
index = handleS(value, result, index, slavoGermanic);
break;
case 'T':
index = handleT(value, result, index);
break;
case 'V':
result.append('F');
index = charAt(value, index + 1) == 'V' ? index + 2 : index + 1;
break;
case 'W':
index = handleW(value, result, index);
break;
case 'X':
index = handleX(value, result, index);
break;
case 'Z':
index = handleZ(value, result, index, slavoGermanic);
break;
default:
index++;
break;
}
}

return alternate ? result.getAlternate() : result.getPrimary();
}

private int handleG(String value,
DoubleMetaphoneResult result,
int index,
boolean slavoGermanic) {
if (charAt(value, index + 1) == 'H') {
index = handleGH(value, result, index);
} else if (charAt(value, index + 1) == 'N') {
if (index == 1 && isVowel(charAt(value, 0)) && !slavoGermanic) {
result.append("KN", "N");
} else if (!contains(value, index + 2, 2, "EY") &&
charAt(value, index + 1) != 'Y' && !slavoGermanic) {
result.append("N", "KN");
} else {
result.append("KN");
}
index = index + 2;
} else if (contains(value, index + 1, 2, "LI") && !slavoGermanic) {
result.append("KL", "L");
index += 2;
} else if (index == 0 && (charAt(value, index + 1) == 'Y' || contains(value, index + 1, 2, ES_EP_EB_EL_EY_IB_IL_IN_IE_EI_ER))) {
//-- -ges-, -gep-, -gel-, -gie- at beginning --//
result.append('K', 'J');
index += 2;
} else if ((contains(value, index + 1, 2, "ER") ||
charAt(value, index + 1) == 'Y') &&
!contains(value, 0, 6, "DANGER", "RANGER", "MANGER") &&
!contains(value, index - 1, 1, "E", "I") &&
!contains(value, index - 1, 3, "RGY", "OGY")) {
//-- -ger-, -gy- --//
result.append('K', 'J');
index += 2;
} else if (contains(value, index + 1, 1, "E", "I", "Y") ||
contains(value, index - 1, 4, "AGGI", "OGGI")) {
//-- Italian "biaggi" --//
if ((contains(value, 0 ,4, "VAN ", "VON ") || contains(value, 0, 3, "SCH")) || contains(value, index + 1, 2, "ET")) {
//-- obvious germanic --//
result.append('K');
} else if (contains(value, index + 1, 4, "IER")) {
result.append('J');
} else {
result.append('J', 'K');
}
index += 2;
} else if (charAt(value, index + 1) == 'G') {
index += 2;
result.append('K');
} else {
index++;
result.append('K');
}
return index;
}

private int handleC(String value,
DoubleMetaphoneResult result,
int index) {
if (conditionC0(value, index)) {  // very confusing, moved out
result.append('K');
index += 2;
} else if (index == 0 && contains(value, index, 6, "CAESAR")) {
result.append('S');
index += 2;
} else if (contains(value, index, 2, "CH")) {
index = handleCH(value, result, index);
} else if (contains(value, index, 2, "CZ") &&
!contains(value, index - 2, 4, "WICZ")) {
//-- "Czerny" --//
result.append('S', 'X');
index += 2;
} else if (contains(value, index + 1, 3, "CIA")) {
//-- "focaccia" --//
result.append('X');
index += 3;
} else if (contains(value, index, 2, "CC") &&
!(index == 1 && charAt(value, 0) == 'M')) {
//-- double "cc" but not "McClelland" --//
return handleCC(value, result, index);
} else if (contains(value, index, 2, "CK", "CG", "CQ")) {
result.append('K');
index += 2;
} else if (contains(value, index, 2, "CI", "CE", "CY")) {
//-- Italian vs. English --//
if (contains(value, index, 3, "CIO", "CIE", "CIA")) {
result.append('S', 'X');
} else {
result.append('S');
}
index += 2;
} else {
result.append('K');
if (contains(value, index + 1, 2, " C", " Q", " G")) {
//-- Mac Caffrey, Mac Gregor --//
index += 3;
} else if (contains(value, index + 1, 1, "C", "K", "Q") &&
!contains(value, index + 1, 2, "CE", "CI")) {
index += 2;
} else {
index++;
}
}

return index;
}

private int handleS(String value,
DoubleMetaphoneResult result,
int index,
boolean slavoGermanic) {
if (contains(value, index - 1, 3, "ISL", "YSL")) {
//-- special cases "island", "isle", "carlisle", "carlysle" --//
index++;
} else if (index == 0 && contains(value, index, 5, "SUGAR")) {
//-- special case "sugar-" --//
result.append('X', 'S');
index++;
} else if (contains(value, index, 2, "SH")) {
if (contains(value, index + 1, 4,
"HEIM", "HOEK", "HOLM", "HOLZ")) {
//-- germanic --//
result.append('S');
} else {
result.append('X');
}
index += 2;
} else if (contains(value, index, 3, "SIO", "SIA") || contains(value, index, 4, "SIAN")) {
//-- Italian and Armenian --//
if (slavoGermanic) {
result.append('S');
} else {
result.append('S', 'X');
}
index += 3;
} else if ((index == 0 && contains(value, index + 1, 1, "M", "N", "L", "W")) || contains(value, index + 1, 1, "Z")) {
//-- german & anglicisations, e.g. "smith" match "schmidt" //
// "snider" match "schneider" --//
//-- also, -sz- in slavic language altho in hungarian it //
//   is pronounced "s" --//
result.append('S', 'X');
index = contains(value, index + 1, 1, "Z") ? index + 2 : index + 1;
} else if (contains(value, index, 2, "SC")) {
index = handleSC(value, result, index);
} else {
if (index == value.length() - 1 && contains(value, index - 2,
2, "AI", "OI")){
//-- french e.g. "resnais", "artois" --//
result.appendAlternate('S');
} else {
result.append('S');
}
index = contains(value, index + 1, 1, "S", "Z") ? index + 2 : index + 1;
}
return index;
}

private boolean conditionC0(String value, int index) {
if (contains(value, index, 4, "CHIA")) {
return true;
} else if (index <= 1) {
return false;
} else if (isVowel(charAt(value, index - 2))) {
return false;
} else if (!contains(value, index - 1, 3, "ACH")) {
return false;
} else {
char c = charAt(value, index + 2);
return (c != 'I' && c != 'E') ||
contains(value, index - 2, 6, "BACHER", "MACHER");
}
}

private int handleL(String value,
DoubleMetaphoneResult result,
int index) {
if (charAt(value, index + 1) == 'L') {
if (conditionL0(value, index)) {
result.appendPrimary('L');
} else {
result.append('L');
}
index += 2;
} else {
index++;
result.append('L');
}
return index;
}

private int handleW(String value,
DoubleMetaphoneResult result,
int index) {
if (contains(value, index, 2, "WR")) {
//-- can also be in middle of word --//
result.append('R');
index += 2;
} else {
if (index == 0 && (isVowel(charAt(value, index + 1)) ||
contains(value, index, 2, "WH"))) {
if (isVowel(charAt(value, index + 1))) {
//-- Wasserman should match Vasserman --//
result.append('A', 'F');
} else {
//-- need Uomo to match Womo --//
result.append('A');
}
index++;
} else if ((index == value.length() - 1 && isVowel(charAt(value, index - 1))) ||
contains(value, index - 1,
5, "EWSKI", "EWSKY", "OWSKI", "OWSKY") ||
contains(value, 0, 3, "SCH")) {
//-- Arnow should match Arnoff --//
result.appendAlternate('F');
index++;
} else if (contains(value, index, 4, "WICZ", "WITZ")) {
//-- Polish e.g. "filipowicz" --//
result.append("TS", "FX");
index += 4;
} else {
index++;
}
}
return index;
}

private int handleT(String value,
DoubleMetaphoneResult result,
int index) {
if (contains(value, index, 4, "TION")) {
result.append('X');
index += 3;
} else if (contains(value, index, 3, "TIA", "TCH")) {
result.append('X');
index += 3;
} else if (contains(value, index, 2, "TH") || contains(value, index,
3, "TTH")) {
if (contains(value, index + 2, 2, "OM", "AM") ||
//-- special case "thomas", "thames" or germanic --//
contains(value, 0, 4, "VAN ", "VON ") ||
contains(value, 0, 3, "SCH")) {
result.append('T');
} else {
result.append('0', 'T');
}
index += 2;
} else {
result.append('T');
index = contains(value, index + 1, 1, "T", "D") ? index + 2 : index + 1;
}
return index;
}

protected static boolean contains(String value, int start, int length,
String[] criteria) {
boolean result = false;
if (start >= 0 && start + length <= value.length()) {
String target = value.substring(start, start + length);

for (int i = 0; i < criteria.length; i++) {
if (target.equals(criteria[i])) {
result = true;
break;
}
}
}
return result;
}

private int handleH(String value,
DoubleMetaphoneResult result,
int index) {
//-- only keep if first & before vowel or between 2 vowels --//
if ((index == 0 || isVowel(charAt(value, index - 1))) &&
isVowel(charAt(value, index + 1))) {
result.append('H');
index += 2;
//-- also takes car of "HH" --//
} else {
index++;
}
return index;
}

private int handleR(String value,
DoubleMetaphoneResult result,
int index,
boolean slavoGermanic) {
if (index == value.length() - 1 && !slavoGermanic &&
contains(value, index - 2, 2, "IE") &&
!contains(value, index - 4, 2, "ME", "MA")) {
result.appendAlternate('R');
} else {
result.append('R');
}
return charAt(value, index + 1) == 'R' ? index + 2 : index + 1;
}

private int handleD(String value,
DoubleMetaphoneResult result,
int index) {
if (contains(value, index, 2, "DG")) {
//-- "Edge" --//
if (contains(value, index + 2, 1, "I", "E", "Y")) {
result.append('J');
index += 3;
//-- "Edgar" --//
} else {
result.append("TK");
index += 2;
}
} else if (contains(value, index, 2, "DT", "DD")) {
result.append('T');
index += 2;
} else {
result.append('T');
index++;
}
return index;
}

private boolean conditionL0(String value, int index) {
if (index == value.length() - 3 &&
contains(value, index - 1, 4, "ILLO", "ILLA", "ALLE")) {
return true;
} else if ((contains(value, value.length() - 2, 2, "AS", "OS") ||
contains(value, value.length() - 1, 1, "A", "O")) &&
contains(value, index - 1, 4, "ALLE")) {
return true;
} else {
return false;
}
}

private int handleP(String value,
DoubleMetaphoneResult result,
int index) {
if (charAt(value, index + 1) == 'H') {
result.append('F');
index += 2;
} else {
result.append('P');
index = contains(value, index + 1, 1, "P", "B") ? index + 2 : index + 1;
}
return index;
}

private boolean isSilentStart(String value) {
boolean result = false;
for (int i = 0; i < SILENT_START.length; i++) {
if (value.startsWith(SILENT_START[i])) {
result = true;
break;
}
}
return result;
}

private boolean conditionM0(String value, int index) {
if (charAt(value, index + 1) == 'M') {
return true;
}
return contains(value, index - 1, 3, "UMB") &&
((index + 1) == value.length() - 1 || contains(value,
index + 2, 2, "ER"));
}

private String cleanInput(String input) {
if (input == null) {
return null;
}
input = input.trim();
if (input.length() == 0) {
return null;
}
return input.toUpperCase(java.util.Locale.ENGLISH);
}

public void append(char primary, char alternate) {
appendPrimary(primary);
appendAlternate(alternate);
}

private int handleAEIOUY(String value, DoubleMetaphoneResult result, int
index) {
if (index == 0) {
result.append('A');
}
return index + 1;
}

protected char charAt(String value, int index) {
if (index < 0 || index >= value.length()) {
return Character.MIN_VALUE;
}
return value.charAt(index);
}

public DoubleMetaphoneResult(int maxLength) {
this.maxLength = maxLength;
}

public void appendAlternate(char value) {
if (this.alternate.length() < this.maxLength) {
this.alternate.append(value);
}
}

public void appendPrimary(char value) {
if (this.primary.length() < this.maxLength) {
this.primary.append(value);
}
}

public void append(char value) {
appendPrimary(value);
appendAlternate(value);
}

private static final String VOWELS = "AEIOUY";

/**
* Prefixes when present which are not pronounced
*/
private static final String[] SILENT_START =
{ "GN", "KN", "PN", "WR", "PS" };

private static boolean contains(String value, int start, int length,
String criteria1, String criteria2,
String criteria3) {
return contains(value, start, length,
new String[] { criteria1, criteria2, criteria3 });
}

private boolean isVowel(char ch) {
return VOWELS.indexOf(ch) != -1;
}

public String getAlternate() {
return this.alternate.toString();
}

public DoubleMetaphone() {
super();
}

private static boolean contains(String value, int start, int length,
String criteria1, String criteria2,
String criteria3, String criteria4) {
return contains(value, start, length,
new String[] { criteria1, criteria2, criteria3,
criteria4 });
}

private static boolean contains(String value, int start, int length,
String criteria1, String criteria2) {
return contains(value, start, length,
new String[] { criteria1, criteria2 });
}

private static boolean contains(String value, int start, int length,
String criteria) {
return contains(value, start, length,
new String[] { criteria });
}

public boolean isComplete() {
return this.primary.length() >= this.maxLength &&
this.alternate.length() >= this.maxLength;
}

private boolean isSlavoGermanic(String value) {
return value.indexOf('W') > -1 || value.indexOf('K') > -1 ||
value.indexOf("CZ") > -1 || value.indexOf("WITZ") > -1;
}

public int getMaxCodeLen() {
return this.maxCodeLen;
}
