public static double sin(double x) {
boolean negative = false;
int quadrant = 0;
double xa;
double xb = 0.0;

/* Take absolute value of the input */
xa = x;
if (x < 0) {
negative = true;
xa = -xa;
}

/* Check for zero and negative zero */
if (xa == 0.0) {
long bits = Double.doubleToLongBits(x);
if (bits < 0) {
return -0.0;
}
return 0.0;
}

if (xa != xa || xa == Double.POSITIVE_INFINITY) {
return Double.NaN;
}

/* Perform any argument reduction */
if (xa > 3294198.0) {
// PI * (2**20)
// Argument too big for CodyWaite reduction.  Must use
// PayneHanek.
double reduceResults[] = new double[3];
reducePayneHanek(xa, reduceResults);
quadrant = ((int) reduceResults[0]) & 3;
xa = reduceResults[1];
xb = reduceResults[2];
} else if (xa > 1.5707963267948966) {
final CodyWaite cw = new CodyWaite(xa);
quadrant = cw.getK() & 3;
xa = cw.getRemA();
xb = cw.getRemB();
}

if (negative) {
quadrant ^= 2;  // Flip bit 1
}

switch (quadrant) {
case 0:
return sinQ(xa, xb);
case 1:
return cosQ(xa, xb);
case 2:
return -sinQ(xa, xb);
case 3:
return -cosQ(xa, xb);
default:
return Double.NaN;
}
}

private double[][] copyOut() {
final int nRows = this.getRowDimension();
final double[][] out = new double[nRows][this.getColumnDimension()];
// can't copy 2-d array in one shot, otherwise get row references
for (int i = 0; i < nRows; i++) {
System.arraycopy(data[i], 0, out[i], 0, data[i].length);
}
return out;
}

private static double sinQ(double xa, double xb) {
int idx = (int) ((xa * 8.0) + 0.5);
final double epsilon = xa - EIGHTHS[idx]; //idx*0.125;

// Table lookups
final double sintA = SINE_TABLE_A[idx];
final double sintB = SINE_TABLE_B[idx];
final double costA = COSINE_TABLE_A[idx];
final double costB = COSINE_TABLE_B[idx];

// Polynomial eval of sin(epsilon), cos(epsilon)
double sinEpsA = epsilon;
double sinEpsB = polySine(epsilon);
final double cosEpsA = 1.0;
final double cosEpsB = polyCosine(epsilon);

// Split epsilon   xa + xb = x
final double temp = sinEpsA * HEX_40000000;
double temp2 = (sinEpsA + temp) - temp;
sinEpsB +=  sinEpsA - temp2;
sinEpsA = temp2;

/* Compute sin(x) by angle addition formula */
double result;

/* Compute the following sum:
*
* result = sintA + costA*sinEpsA + sintA*cosEpsB + costA*sinEpsB +
*          sintB + costB*sinEpsA + sintB*cosEpsB + costB*sinEpsB;
*
* Ranges of elements
*
* xxxtA   0            PI/2
* xxxtB   -1.5e-9      1.5e-9
* sinEpsA -0.0625      0.0625
* sinEpsB -6e-11       6e-11
* cosEpsA  1.0
* cosEpsB  0           -0.0625
*
*/

//result = sintA + costA*sinEpsA + sintA*cosEpsB + costA*sinEpsB +
//          sintB + costB*sinEpsA + sintB*cosEpsB + costB*sinEpsB;

//result = sintA + sintA*cosEpsB + sintB + sintB * cosEpsB;
//result += costA*sinEpsA + costA*sinEpsB + costB*sinEpsA + costB * sinEpsB;
double a = 0;
double b = 0;

double t = sintA;
double c = a + t;
double d = -(c - a - t);
a = c;
b = b + d;

t = costA * sinEpsA;
c = a + t;
d = -(c - a - t);
a = c;
b = b + d;

b = b + sintA * cosEpsB + costA * sinEpsB;
/*
t = sintA*cosEpsB;
c = a + t;
d = -(c - a - t);
a = c;
b = b + d;

t = costA*sinEpsB;
c = a + t;
d = -(c - a - t);
a = c;
b = b + d;
*/

b = b + sintB + costB * sinEpsA + sintB * cosEpsB + costB * sinEpsB;
/*
t = sintB;
c = a + t;
d = -(c - a - t);
a = c;
b = b + d;

t = costB*sinEpsA;
c = a + t;
d = -(c - a - t);
a = c;
b = b + d;

t = sintB*cosEpsB;
c = a + t;
d = -(c - a - t);
a = c;
b = b + d;

t = costB*sinEpsB;
c = a + t;
d = -(c - a - t);
a = c;
b = b + d;
*/

if (xb != 0.0) {
t = ((costA + costB) * (cosEpsA + cosEpsB) -
(sintA + sintB) * (sinEpsA + sinEpsB)) * xb;  // approximate cosine*xb
c = a + t;
d = -(c - a - t);
a = c;
b = b + d;
}

result = a + b;

return result;
}

