/**
* Build an initial simplex.
*
* @param startPoint First point of the simplex.
* @throws DimensionMismatchException if the start point does not match
* simplex dimension.
*/
public void build(final double[] startPoint) {
if (dimension != startPoint.length) {
throw new DimensionMismatchException(dimension, startPoint.length);
}

// Set first vertex.
simplex = new PointValuePair[dimension + 1];
simplex[0] = new PointValuePair(startPoint, Double.NaN);

// Set remaining vertices.
for (int i = 0; i < dimension; i++) {
final double[] confI = startConfiguration[i];
final double[] vertexI = new double[dimension];
for (int k = 0; k < dimension; k++) {
vertexI[k] = startPoint[k] + confI[k];
}
simplex[i + 1] = new PointValuePair(vertexI, Double.NaN);
}
}

public static double max(final double a, final double b) {
if (a > b) {
return a;
}
if (a < b) {
return b;
}
/* if either arg is NaN, return NaN */
if (a != b) {
return Double.NaN;
}
/* min(+0.0,-0.0) == -0.0 */
/* 0x8000000000000000L == Double.doubleToRawLongBits(-0.0d) */
long bits = Double.doubleToRawLongBits(a);
if (bits == 0x8000000000000000L) {
return b;
}
return a;
}

private static double atan(double xa, double xb, boolean leftPlane) {
boolean negate = false;
int idx;

if (xa == 0.0) { // Matches +/- 0.0; return correct sign
return leftPlane ? copySign(Math.PI, xa) : xa;
}

if (xa < 0) {
// negative
xa = -xa;
xb = -xb;
negate = true;
}

if (xa > 1.633123935319537E16) { // Very large input
return (negate ^ leftPlane) ? (-Math.PI * F_1_2) : (Math.PI * F_1_2);
}

/* Estimate the closest tabulated arctan value, compute eps = xa-tangentTable */
if (xa < 1) {
idx = (int) (((-1.7168146928204136 * xa * xa + 8.0) * xa) + 0.5);
} else {
final double oneOverXa = 1 / xa;
idx = (int) (-((-1.7168146928204136 * oneOverXa * oneOverXa + 8.0) * oneOverXa) + 13.07);
}
double epsA = xa - TANGENT_TABLE_A[idx];
double epsB = -(epsA - xa + TANGENT_TABLE_A[idx]);
epsB += xb - TANGENT_TABLE_B[idx];

double temp = epsA + epsB;
epsB = -(temp - epsA - epsB);
epsA = temp;

/* Compute eps = eps / (1.0 + xa*tangent) */
temp = xa * HEX_40000000;
double ya = xa + temp - temp;
double yb = xb + xa - ya;
xa = ya;
xb += yb;

//if (idx > 8 || idx == 0)
if (idx == 0) {
/* If the slope of the arctan is gentle enough (< 0.45), this approximation will suffice */
//double denom = 1.0 / (1.0 + xa*tangentTableA[idx] + xb*tangentTableA[idx] + xa*tangentTableB[idx] + xb*tangentTableB[idx]);
final double denom = 1d / (1d + (xa + xb) * (TANGENT_TABLE_A[idx] + TANGENT_TABLE_B[idx]));
//double denom = 1.0 / (1.0 + xa*tangentTableA[idx]);
ya = epsA * denom;
yb = epsB * denom;
} else {
double temp2 = xa * TANGENT_TABLE_A[idx];
double za = 1d + temp2;
double zb = -(za - 1d - temp2);
temp2 = xb * TANGENT_TABLE_A[idx] + xa * TANGENT_TABLE_B[idx];
temp = za + temp2;
zb += -(temp - za - temp2);
za = temp;

zb += xb * TANGENT_TABLE_B[idx];
ya = epsA / za;

temp = ya * HEX_40000000;
final double yaa = (ya + temp) - temp;
final double yab = ya - yaa;

temp = za * HEX_40000000;
final double zaa = (za + temp) - temp;
final double zab = za - zaa;

/* Correct for rounding in division */
yb = (epsA - yaa * zaa - yaa * zab - yab * zaa - yab * zab) / za;

yb += -epsA * zb / za / za;
yb += epsB / za;
}


epsA = ya;
epsB = yb;

/* Evaluate polynomial */
final double epsA2 = epsA * epsA;

/*
yb = -0.09001346640161823;
yb = yb * epsA2 + 0.11110718400605211;
yb = yb * epsA2 + -0.1428571349122913;
yb = yb * epsA2 + 0.19999999999273194;
yb = yb * epsA2 + -0.33333333333333093;
yb = yb * epsA2 * epsA;
*/

yb = 0.07490822288864472;
yb = yb * epsA2 + -0.09088450866185192;
yb = yb * epsA2 + 0.11111095942313305;
yb = yb * epsA2 + -0.1428571423679182;
yb = yb * epsA2 + 0.19999999999923582;
yb = yb * epsA2 + -0.33333333333333287;
yb = yb * epsA2 * epsA;


ya = epsA;

temp = ya + yb;
yb = -(temp - ya - yb);
ya = temp;

/* Add in effect of epsB.   atan'(x) = 1/(1+x^2) */
yb += epsB / (1d + epsA * epsA);

//result = yb + eighths[idx] + ya;
double za = EIGHTHS[idx] + ya;
double zb = -(za - EIGHTHS[idx] - ya);
temp = za + yb;
zb += -(temp - za - yb);
za = temp;

double result = za + zb;
double resultb = -(result - za - zb);

if (leftPlane) {
// Result is in the left plane
final double pia = 1.5707963267948966 * 2;
final double pib = 6.123233995736766E-17 * 2;

za = pia - result;
zb = -(za - pia + result);
zb += pib - resultb;

result = za + zb;
resultb = -(result - za - zb);
}


if (negate ^ leftPlane) {
result = -result;
}

return result;
}

